mapper.update:
update PUBLIC.CHARACTERS
set POSITION = POSITION + 1
where POSITION >= #{startPosition,jdbcType=INTEGER} and POSITION < #{endPosition,jdbcType=INTEGER} and MAIN_CHARACTER = #{mainCharacter,jdbcType=CHAR}

update PUBLIC.CHARACTERS
set POSITION = POSITION - 1
where POSITION > #{startPosition,jdbcType=INTEGER} and POSITION <= #{endPosition,jdbcType=INTEGER} and MAIN_CHARACTER = #{mainCharacter,jdbcType=CHAR}
